css:
@charset "UTF-8";
@import url('https://rsms.me/inter/inter.css');
@import url('https://fonts.googleapis.com/css?family=Roboto+Mono:400,700');

/* CSS RESET */

html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed, 
figure, figcaption, footer, header, hgroup, 
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
	margin: 0;
	padding: 0;
	border: 0;
	font-size: 100%;
	font: inherit;
	vertical-align: baseline;
}
/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure, 
footer, header, hgroup, menu, nav, section {
	display: block;
}
body {
	line-height: 1;
}
ol, ul {
	list-style: none;
}
blockquote, q {
	quotes: none;
}
blockquote:before, blockquote:after,
q:before, q:after {
	content: '';
	content: none;
}
table {
	border-collapse: collapse;
	border-spacing: 0;
}

/* CSS Document */

@media print {
  @page {
    size: A4;
    margin: 1.27cm 0 1.27cm 0; /* Set margin on each page */
  }
  body {
    font-family: 'Inter', sans-serif;
    font-size: 16px;
    color: #444444;
  }
}

.print-format {
	padding: 0 !important;
}

.header {
	display: flex;
  align-items: center;
  justify-content: space-between;
	width: 100%;
  padding: 0 1.27cm 1cm 1.27cm;
  box-sizing: border-box;
}

.header .logo {
	height: 2cm;
}

.header .logo-calilait {
	height: 1.5cm;
}

.header h1 {
  font-size: 1.777rem;
  font-weight: bold;
}

.line {
  margin-top: 0.5cm;
	padding: 0.5cm 1.27cm;
	box-sizing: border-box;
}

.line.border {
  border-top: 1px solid #a9a9a9;
}

.line.no-top-margin {
  margin-top: 0;
}

.columns {
	display: flex;
}

.line > div:not(:first-child) {
  margin-left: 0.5cm;
}

.line .data-field:not(:first-child) {
  margin-top: 0.5cm;
}

.line .data-field > label {
  margin: 0.25cm 0;
  font-size: 0.75rem;
  font-weight: bold;
  color: #a9a9a9;
}

.line-no-break {
	page-break-inside: avoid;
}

.text-big {
  font-size: 1.333rem;
}

.text-bold {
  font-weight: bold;
}

.text-uppercase {
	text-transform: uppercase;
}

.text-grey-dark {
  color: #a9a9a9;
}

.text-right {
	text-align: right;
}

.text-center {
  text-align: center;
}

p {
  line-height: 1.5;
}

.table {
  display: table;
  width: 100%;
  margin: 0;
}

.table-header {
  display: flex;
  padding: 0.5cm 0;
  box-sizing: border-box;
  background-color: #dfdfdf;
}

.table-header > h1 {
  color: #929292;
}

.table-header > h1:first-child {
  margin-left: 1.27cm;
}

.table-header > h1:last-child {
  margin-right: 1.27cm;
}

.table-body > .table-row {
  display: flex;
  padding: 0.125cm 0;
  box-sizing: border-box;
  font-family: 'Roboto Mono', monospace;
  line-height: 1.25;
  border-bottom: 1px solid #a9a9a9;
  -webkit-column-break-inside: avoid;
  page-break-inside: avoid;
  break-inside: avoid;
}

.table-body > .table-row:first-child {
  padding-top: 0.25cm;
}

.table-body > .table-row > div:first-child {
  margin-left: 1.27cm;
}

.table-body > .table-row > div:last-child {
  margin-right: 1.27cm;
}

.table-footer {
  display: flex;
  margin: 0.25cm 0;
  padding: 0.25cm 1.27cm;
  box-sizing: border-box;
  font-size: 0.75rem;
}

.table-footer label {
  font-weight: bold;
  padding-bottom: 0.25cm;
}

.payment-details {
	width: 100%;
}

.payment-details > tbody td:not(:first-child) {
	border-left: 1px solid #ffffff;
}

.payment-details > thead td {
	background-color: #9e1e23;
}

.payment-details > tbody td {
	background-color: #eed6d6;
	line-height: 1.5;
}

.payment-details > tbody td label {
	color: #9e1e23;
	text-transform: uppercase;
}

.terms {
  padding: 1.9cm;
  page-break-before: always;
}

.terms > .title {
	width: 100%;
	font-size: 12px;
	font-weight: bold;
	text-align: center;
}

.terms > .section {
	font-size: 11px;
	line-height: 1;
}

.terms > .section > h1 {
	font-size: 11px;
	font-weight: bold;
	margin: 10px 0 5px 0;
}

.terms > .section ol {
	counter-reset: list;
	padding-left: 0;
}

.terms > .section ol > li {
	list-style: none;
}

.terms > .section ol > li:before {
  content: "(" counter(list, decimal) ") ";
	counter-increment: list;
	padding-right: 0.25cm;
}

.page-footer {
  font-size: 12px;
}

/* Width */

.w-auto {
  flex-grow: 1;
}

.w-third {
  width: 33.3333%;
}

.w-1 {
  width: 8.3333%;
}

.w-2 {
  width: 16.6667%;
}

.w-3 {
  width: 25%;
}

.w-4 {
  width: 33.3333%;
}

.w-5 {
  width: 41.6667%;
}

.w-6 {
  width: 50%;
}

.w-8 {
  width: 66.6667%;
}

.w-12 {
  width: 100%;
}

