{% set bank_details = frappe.get_all('Bank Account', filters={'parent': 'RK Group SPRL'}, fields=['iban', 'bic']) %}
{% set payment_date = frappe.utils.add_days(doc.due_date, -3) %}
{% set other_charges_total = 0.0 %}
{%- for row in doc.taxes -%}
  {% if "T.V.A." not in row.account_head %}
    {% set other_charges = true %}
  {% endif %}
{%- endfor -%}
<!doctype html>
<html>
<head>
<meta charset="UTF-8">
<meta http-equiv="X-UA-Compatible" content="IE=edge">
<meta name="viewport" content="width=device-width, initial-scale=1">
<title>Facture (allemand)</title>
<link rel="stylesheet" href="sales_invoice.css">
</head>
<body>
	{% if letter_head and not no_letterhead %}
    {{ letter_head }}
  {% endif %}

  <div class="line no-top-margin columns">
    <div class="w-4">
      <div class="data-field">
        <label>{{ _("Customer") }}</label>
        <p>
          <span class="text-bold">{{ doc.customer_name }}</span><br>
          {{ doc.address_display }}<br>
          {% if doc.tax_id %}
          {{ _("VAT ID") }}: {{ doc.tax_id }}
          {% endif %}
        </p>
      </div>
    </div>
    <div class="w-4">
      <div class="data-field">
        <label>{{ _("Delivered to") }}</label>
        <p>
          <span class="text-bold">{{ doc.shipping_address_name }}</span><br>
          {% if doc.shipping_address %}
            {{ doc.shipping_address }}
          {% else %}
           {{ _("Same as billing address") }}
          {% endif %}
        </p>
      </div>
    </div>
    <div class="w-4">
      <div class="data-field">
        <label>{{ _("Date") }}</label>
        <p>{{ doc.get_formatted("posting_date") }}</p>
      </div>
      <div class="data-field">
        <label>{{ _("Number") }}</label>
        <p>{{ doc.name }}</p>
      </div>
    </div>
  </div>

  {% if doc.status != "Paid" %}
  <div class="line border no-top-margin columns">
    <div class="w-4">
      <div class="data-field">
        <label>{{ _("Pay before") }}</label>
        <p class="text-bold text-big">
          {{ frappe.utils.formatdate(payment_date, "dd/MM/YYYY") }}
        </p>
      </div>
      <div class="data-field">
        <label>{{ _("Bank account (IBAN)") }}</label>
        <p>
            {{ bank_details[0].get("iban") }}
        </p>
      </div>
    </div>
    <div class="w-4">
      <div class="data-field">
        <label>{{ _("Message") }}</label>
        <p class="text-bold text-big">
          {{ doc.name }}
        </p>
      </div>
      <div class="data-field">
        <label>{{ _("BIC") }}</label>
        <p>
          {{ bank_details[0].get("bic") }}
        </p>
      </div>
    </div>
    <div class="w-4">
      <div class="data-field">
        <label>{{ _("Amount") }}</label>
        <p class="text-bold text-big">
          {{ doc.get_formatted("grand_total") }}
        </p>
      </div>
    </div>
  </div>
  {% endif %}

  <div class="table">
    <div class="table-header">
      <h1 class="w-1">{{ _("Code") }}</h1>
      <h1 class="w-6">{{ _("Name") }}</h1>
      <h1 class="w-1">{{ _("Qty") }}</h1>
      <h1 class="w-1">{{ _("UOM") }}</h1>
      {% if doc.print_discount %}
        <h1 class="w-1">{{ _("Price") }}</h1>
        <h1 class="w-1">{{ _("Discount") }}</h1>
      {% endif %}
      <h1 class="w-1 text-right">{{ _("Your Price") }}</h1>
    </div>
    <div class="table-body">
      {%- for row in doc.items -%}
        {% set barcode = frappe.db.get_value("Item", row.item_code, "barcode") %}
        <div class="table-row">
          <div class="w-1">{{ row.item_code}}</div>
          <div class="w-6">
            {{ _(row.item_name) }}<br>
            <i>Barcode {{ barcode }}</i> - <i>Batch {{ row.batch_no }}</i>
          </div>
          <div class="w-1">{{ row.get_formatted("qty", doc) }}</div>
          <div class="w-1">{{ _(row.uom) }}</div>
          {% if doc.print_discount %}
            {% if row.price_list_rate >= row.rate %}
            <div class="w-1">{{ row.get_formatted("price_list_rate", doc) }}</div>
            {% else %}
            <div class="w-1">{{ row.get_formatted("rate", doc) }}</div>
          {% endif %}
          <div class="w-1">{{ row.get_formatted("discount_percentage", doc) }}</div>
          {% endif %}
          <div class="w-1 text-right">{{ row.get_formatted("rate", doc) }}</div>
        </div>
      {%- endfor -%}
    </div>
    <div class="table-footer">
      <div class="w-auto">
        <label>{{ _("Total VAT excl.") }}</label>
        {% if doc.print_discount %}
          {{ doc.get_formatted("total_price_list") }}
        {% else %}
          {{ doc.get_formatted("total") }}
        {% endif %}
      </div>
      {% if doc.print_discount %}
      <div class="w-auto">
        <label class="text-bold">{{ _("Discount") }}</label>
        {{ doc.get_formatted("total_discount") }}
      </div>
      {% endif %}
      <div class="w-auto">
        <label class="text-bold">{{ _("VAT") }}</label>
        {%- for row in doc.taxes -%}
          {% if "T.V.A." in row.account_head %}
            {{ row.rate }}% : {{ frappe.utils.fmt_money(row.tax_amount, 2, doc.currency) }}<br>
            {% set vat_present = true %}
          {% endif %}
        {%- endfor -%}
        {% if vat_present != true %}
          {{ frappe.utils.fmt_money(0.0, 2, doc.currency) }}
        {% endif %}
      </div>
      {% if other_charges %}
      <div class="w-auto">
        <label class="text-bold">{{ _("Other") }}</label>
        {%- for row in doc.taxes -%}
          {% if "T.V.A." not in row.account_head %}
            {{ row.rate }}% : {{ frappe.utils.fmt_money(row.tax_amount, 2, doc.currency) }}<br>
          {% endif %}
        {%- endfor -%}
      </div>
      {% endif %}
      <div class="w-auto">
        <label class="text-bold">{{ _("Total") }}</label>
        {{ doc.get_formatted("grand_total") }}
      </div>
    </div>
  </div>

  <footer class="line border text-center">
    <p>
      <span class="text-bold">RK Group SPRL</span> - 
      Rue des Praules 2, 5030 Gembloux, Belgium - 
      {{ _("VAT ID") }}: BE 0863.961.875 - <br>
      {{ _("Telephone") }}: +32 81 980 070 - 
      {{ _("E-mail") }}: accounting@rkgroup.be <br>
      {% if "Baladia" in doc.letter_head %}
        {{ _("Baladia is a trademark of RK Group SPRL.") }}
      {% else %}
        {{ _("Calilait is a trademark of RK Group SPRL.") }}
      {% endif %}
    </p>
  </footer>

  <div class="terms">
    {{ doc.terms }}
  </div>

</body>
</html>
